mapper.select:
SELECT od.id as orderId, od.created_time as createdTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, od.is_delete as isDelete, od.is_comment as isComment FROM orders od LEFT JOIN order_status os ON od.id = os.order_id WHERE od.user_id = #{paramsMap.userId}
and os.order_status = #{paramsMap.orderStatus}
AND od.is_delete = 0 ORDER BY od.created_time desc

SELECT
*
FROM
`orders`


      and  id=#{id}


      and  user_id=#{userId}


     and   is_delete=#{isDelete}


ORDER BY created_time DESC LIMIT 1

SELECT od.id as orderId, od.created_time as createdTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, oi.item_id as itemId, oi.item_name as itemName, oi.item_spec_id as itemSpecId , oi.item_spec_name as itemSpecName, oi.price as price , oi.buy_counts as buyCounts, od.is_delete as isDelete, oi.item_img as itemImg
FROM orders od LEFT JOIN order_status os ON od.id = os.order_id LEFT JOIN order_items oi ON od.id = oi.order_id WHERE od.user_id = #{paramsMap.userId}
and os.order_status = #{paramsMap.orderStatus}
AND od.is_delete = 0 ORDER BY od.updated_time asc
